let head = document.getElementById('header');

function eventsHearder(element, event) {

    event.split(' ').forEach(e => {

        element.addEventListener(e, () => {
            if (e == 'mousemove') {
                element.style.backgroundColor = '#017cff';
            } else if (e == 'mouseout') {
                element.style.backgroundColor = '#014792';
            }
        });

    });

}


eventsHearder(head, 'mousemove mouseout');